flagAustria :: Attribute -> Html ()
flagAustria clipId =
    g_ [clipId, fill_rule_ "evenodd"] $ do
        path_ [fill_ "#fff", d_ "M512 512H0V0h512z"]
        path_ [fill_ "#df0000", d_ "M512 512H0V341.3h512zm0-341.2H0V0h512z"]

